import React, {useState, useEffect} from 'react'
import {Link, useHistory, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService'

const AddEmployeeComponent = () => {

    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')
    const [emailId, setEmailId] = useState('')
    const [resReason, setResReason] = useState('')
    const history = useHistory();
    const {id} = useParams();

    const saveOrUpdateEmployee = (e) => {
        e.preventDefault();

        const employee = {firstName, lastName, emailId}

        if(id){
            EmployeeService.updateEmployee(id, employee).then((response) => {
                history.push('/employees')
            }).catch(error => {
                console.log(error)
            })

        }else{
            EmployeeService.createEmployee(employee).then((response) =>{

                console.log(response.data)

                history.push('/employees');

            }).catch(error => {
                console.log(error)
            })
        }

    }

    useEffect(() => {

        EmployeeService.getEmployeeById(id).then((response) =>{
            setFirstName(response.data.firstName)
            setLastName(response.data.lastName)
            setEmailId(response.data.emailId)
        }).catch(error => {
            console.log(error)
        })
    }, [])

    const title = () => {
          return <h3 className = "text-center">Employee Resignation Information</h3>
    }
    const titleResignation = () => {
        return <h3 className = "text-center">Resignation Information</h3>
    }

    return (
        <div>
           <br /><br />
           <div className = "container">
                <div className = "row">
                    <div className = "card col-md-6 offset-md-3 offset-md-3">
                       {
                           title()
                       }
                        <div className = "card-body">
                            <form>
                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Employee Name : Rohit</label>
                                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    <label className = "form-label"> Employee Id : 602</label>

                                </div>

                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Level : 5</label>
                                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    <label className = "form-label"> Designation : Staff Engineer</label>
                                </div>

                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Date of joining : 25-11-2019</label>
                                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    <label className = "form-label"> Date of Resignation : 07-Dec-2021</label>
                                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                    <label className = "form-label"> Last working day : 25-Feb-2022</label> 
                                </div>

                            </form>

                        </div>
                    </div>
                    <div className = "card col-md-6 offset-md-3 offset-md-3">
                       {
                           titleResignation()
                       }
                        <div className = "card-body">
                            <form>
                                <div className = "form-group mb-2">
                                    <label className = "form-label"> Date of Resignation : 07-Dec-2021</label>
                                </div>    
                                <div className = "form-group mb-2">    
                                    <label className = "form-label"> Reason : </label> 
                                    <input
                                        type = "text"
                                        placeholder = "Enter reason"
                                        name = "resReason"
                                        className = "form-control"
                                        value = {resReason}
                                        onChange = {(e) => setResReason(e.target.value)}
                                    >
                                    </input>
                                </div>

                                <button className = "btn btn-success" onClick = {(e) => saveOrUpdateEmployee(e)} >Submit </button>
                                <Link to="/login" className="btn btn-danger"> Cancel </Link>
                            </form>

                        </div>
                    </div>
                </div>

           </div>

        </div>
    )
}

export default AddEmployeeComponent
